#include "../items/weapons.h"#include "event_weapon.h"#include "../sprites/babar.h"#include "../util/collisions.h"#include "../util/debug.h"Event_weapon::Event_weapon(Babar *target){	m_target = target;	m_pos.x = target->position_x() + 200;	m_pos.y = 1770;	m_pos.h = 50;	m_pos.w = 50;	m_weapon_type =  SHOTGUN;}Event_weapon::~Event_weapon(){	}bool Event_weapon::can_start(){	return check_collision(m_pos, m_target->position());}void Event_weapon::start(){	m_target->change_weapon(m_weapon_type);}