/** * 	@file special_letter.cpp * 	@brief Implementation de la classe Special_letter * * 	@author Guillaume BÃ©rard & Benoit Morel * 	@date juin 2011 * */#include "special_letter.h"#include "../util/debug.h"#include "../video/font.h"#include "../../lib/SDL/include/SDL/SDL.h"#include "../../lib/SDL/include/SDL/SDL_ttf.h"Special_letter::Special_letter(char c, int size, int r, int g, int b, std::string font_name):	m_letter(c),	m_offset_size(14),	m_size(size),	m_r(r),	m_g(g),	m_b(b),	m_font_name(font_name){	std::string str;	str += m_letter;	init(str, m_size + m_offset_size, m_r, m_g, m_b, m_font_name);}Special_letter::~Special_letter(){	}void Special_letter::update(){ 	if (m_offset_size == 0 )		return;	if (m_offset_size > 3) {		m_offset_size -= 4;	} else {		m_offset_size = 0;	}	std::string str;	str += m_letter;	init(str, m_size + m_offset_size, m_r, m_g, m_b, m_font_name);	}int Special_letter::fake_w(){	return (double(m_size) / double(m_size + m_offset_size)) * double(Surface_text::w());}int Special_letter::fake_h(){	return (double(m_size) / double(m_size + m_offset_size)) * double(Surface_text::h());}